class ResearchAgent:
    def research(self, question: str) -> str:
        results = []
        query = self.formulate_query(question)

        while not self.is_sufficient(results):
            search_results = self.search(query)
            analysis = self.analyze(search_results)
            results.extend(analysis)
            query = self.refine_query(query, analysis)

        return self.synthesize(results)
