class CriticGPT:
    def review(self, code: str) -> Report:
        bugs = self.detect_bugs(code)
        security = self.security_audit(code)
        quality = self.quality_check(code)

        return Report(
            issues=bugs + security,
            quality_score=quality,
            recommendations=self.suggest_fixes(bugs)
        )

