# Tool works the same for human and agent
def commit_tool(message: str, files: list = None):
    """Commit changes - callable by human CLI or agent"""
    if files:
        git.add(files)
    else:
        git.add('.')
    git.commit(message)
    return {'status': 'committed', 'message': message}

# Human: /commit "Fix bug"
# Agent: tool_call('commit', {message: 'Fix bug'})
