async def execute_with_approval(action):
    risk = assess_risk(action)

    if risk == 'high':
        approved = await slack.request_approval(
            channel='#agent-approvals',
            message=f'Approve: {action.description}?'
        )
        if not approved:
            return {'status': 'rejected'}

    return await action.execute()
