class LATS:
    def search(self, problem, max_iterations=50):
        root = Node(problem)
        for _ in range(max_iterations):
            node = self.select(root)  # UCB
            actions = self.expand(node)  # LLM generates
            value = self.evaluate(node)  # LLM judges
            self.backpropagate(node, value)
        return self.best_path(root)
