class LayeredConfig:
    def __init__(self):
        self.global_config = load('~/.agent/config')
        self.project_config = load('.agent/config')

    def get_config(self, task_overrides=None):
        config = {**self.global_config}
        config.update(self.project_config)
        if task_overrides:
            config.update(task_overrides)
        return config
