class AgentCapabilities:
    def __init__(self, tier: int):
        self.tier = tier

    async def process(self, task):
        research = await self.research(task)  # Tier 1

        if self.tier >= 2:
            qualified = await self.qualify(research)
            email = await self.draft_email(qualified)

            if self.tier >= 3 and qualified.confidence > 0.8:
                return await self.auto_send(email)
            return await self.request_approval(email)

        return self.present_findings(research)
