class ProgressiveToolLoader:
    def __init__(self):
        self.tool_registry = {}  # Not loaded yet
        self.loaded_tools = {}

    def get_tool(self, task_type):
        if task_type not in self.loaded_tools:
            tool_name = self.discover(task_type)
            self.loaded_tools[task_type] = self.load(tool_name)
        return self.loaded_tools[task_type]
