class InteractionModes:
    TAB_COMPLETE = 'low_autonomy'    # Human drives
    COMMAND_K = 'medium_autonomy'    # Human defines scope
    AGENT = 'high_autonomy'          # Agent multi-step
    BACKGROUND = 'full_autonomy'     # Agent async, entire PRs

# User can switch modes fluidly
if task.complexity == 'simple':
    use_mode(InteractionModes.TAB_COMPLETE)
else:
    use_mode(InteractionModes.AGENT)
