def tree_of_thought(problem, depth=3):
    branches = generate_branches(problem)
    evaluated = []
    for branch in branches:
        score = evaluate(branch)
        if score > threshold:
            if depth > 0:
                sub_branches = tree_of_thought(branch, depth-1)
                evaluated.extend(sub_branches)
            else:
                evaluated.append((branch, score))
    return select_best(evaluated)
