class VerboseAgent:
    def execute(self, task, verbose=True):
        steps = []

        step = self.analyze(task)
        if verbose: steps.append(f'Analyzing: {step}')

        files = self.search(step)
        if verbose: steps.append(f'Found {len(files)} files')

        plan = self.plan(files)
        if verbose: steps.append(f'Plan: {plan}')

        result = self.run(plan)
        if verbose: steps.append(f'Result: {result}')

        return {'result': result, 'reasoning': steps}
